INSERT INTO `song` VALUES (199, 53, '平井大-願いごと', '願いごと', '2022-04-14 18:04:53', '2022-04-26 20:53:46', '/img/songPic/song.jpg', '[00:00.000] 作词 : EIGO(ONEly Inc.)/Dai Hirai\r\n[00:00.303] 作曲 : Dai Hirai\r\n[00:00.607] Sorry ごめんねいつでも\r\n[00:03.508] 隣にいてくれたのに\r\n[00:06.333] \"I\'m me\" なんて言ってさ\r\n[00:09.097] ー人よがり最悪だ\r\n[00:10.957]\r\n[00:11.612] やりたいことしか出来ないし\r\n[00:14.385] その上、不器用で面倒くさいし\r\n[00:17.566] そんな落ちこぼれな Cowboy が\r\n[00:20.505] シンデレラに恋したのさ\r\n[00:24.637]\r\n[00:25.853] 名もなきメロディーをキミに贈ろう\r\n[00:31.448] これは Little song for U\r\n[00:32.846]\r\n[00:33.492] 待ち合わせてデートに行こう\r\n[00:36.402] 抱き合って何度もキスをしよう\r\n[00:39.185] ふざけて寝転がって見上げた\r\n[00:41.975] 視線の先広がる星空\r\n[00:44.435]\r\n[00:44.908] Let\'s 願いごとをしてそれを言い合おう\r\n[00:48.334] なんて言うけど、あ一ね\r\n[00:50.481] 急に「どうかこんな時間を...永遠に与えてください」\r\n[00:55.597] なんて言えない\r\n[00:57.947]\r\n[00:59.318] 気持ちを伝えられる勇気をください\r\n[01:02.631]\r\n[01:08.569] とっくにわかっているし\r\n[01:11.336] どっぷりハマっているよ\r\n[01:14.214] とっちらかってるココロ\r\n[01:16.978] どっちにしても I love it\r\n[01:18.825]\r\n[01:19.322] やきもちとかキャラじゃないものを\r\n[01:22.268] 焼いて焦げ付かせ困ってんだ\r\n[01:25.292] キレイなモノでもないけど\r\n[01:28.310] いつか、もらって欲しい\r\n[01:31.574]\r\n[01:33.142] こんな名もなきメロディーをリングの代わりに\r\n[01:38.843] タイトルはまだないけど\r\n[01:40.765]\r\n[01:41.276] 待ち合わせてデートに行こう\r\n[01:44.128] 抱き合って何度もキスをしよう\r\n[01:46.901] 車のシート倒してみれば\r\n[01:49.716] 視線の先にまたも星空\r\n[01:52.124]\r\n[01:52.736] Let\'s 願いごとをして、今日は言い合おう\r\n[01:56.042] 言いづらいけど、ゆ一ね\r\n[01:58.266] 欲しいのは「どうかこの気持ちを·...キミに伝えられる勇気」\r\n[02:02.925] なんだってことを\r\n[02:05.734]\r\n[02:15.108] たまにケンカして仲直りして\r\n[02:17.982] いつか誓い交わすキスをしよう\r\n[02:20.774] 同じ屋根の下手を繋いだ\r\n[02:23.543] 二人を祝福する星空\r\n[02:25.960]\r\n[02:26.557] Let\'s 願いごとをしてそれを言い合おう\r\n[02:29.913] 出来ればお揃いがい一ね\r\n[02:32.130] そうだ！「どうかこんな時間を·...永遠に与えてください」\r\n[02:37.197] なんてどうだろ？\r\n[02:40.034]\r\n[02:40.886] この曲のタイトルは「願いごと」にしよう\r\n[02:45.467]\r\n[02:46.939] そして死ぬまで書き続けていこう', '/song/1649930693640.mp3', NULL);
