INSERT INTO `song` VALUES (392, 106, 'CREAM D- INTRO', 'LIFE AFTER LIFE', '2022-04-15 21:04:58', '2022-04-15 21:04:58', '/img/songPic/song.jpg', '[00:00.000] 作词 : CREAM D\r\n[00:01.000] 作曲 : CREAM D\r\n[00:02.000] 编曲 : 派克特（PACT）\r\n[00:21.89]把无穷的Knowledge 给我\r\n[00:23.96]把无尽的灵感也 给我\r\n[00:26.20]把无限的敬畏都注入我 人 格\r\n[00:28.51]It\'s jesus make me know\r\n[00:30.16]那天才与Soldiers cover me\r\n[00:32.15]那天才与Artist come with me\r\n[00:34.13]那天才为天父的骄傲才拿起笔 Loyalty inside my DNA\r\n[00:38.506] 混音 : 派克特（PACT）\r\n[00:42.999] 母带 : 派克特（PACT）', '/song/1650027898727.mp3', NULL);
