INSERT INTO `song_list` VALUES (6, '林俊杰的正确打开方式【路人请参考简介】', '/img/songListPic/165088728736161a6cfbf-7d20-48a6-8740-29d101b7a975.jpg', '这是一个一定要顺序播放并且不切歌才能发现其中奥妙的歌单。\n这是一个可以完美呈现林俊杰音乐态度的歌单。\n这是一个林俊杰的立体化打开方式。', '华语');
