INSERT INTO `song_list` VALUES (32, '『空灵欧美』论◎怎样唱出星辰大海', '/img/songListPic/1650888592458f090cc9d-c61d-4d3d-acc4-6a1ab4d66eb9.jpg', '“我们在哪里？”\n\n“星空，是星空！美丽而触手可及！”\n\n“这里，是星辰大海，是你小时候的幻想……”', '欧美');
