INSERT INTO `song_list` VALUES (34, '那些超带感的英文歌曲~『一秒沦陷』', '/img/songListPic/1650888606477fef49f07-7847-4143-8a86-32243f1acec4.jpg', '每天，都要去去做一些枯燥的事\n一些让人心烦的事\n可是又不得不去做\n一切都在重复\n都在复制、粘贴，复制、粘贴……\n何必不去做那些让人愉快的事呢\n比如说\n听歌\n它能让生活有趣起来\n有意义起来\n那么就去欣赏...', '欧美');
INSERT INTO `song_list` VALUES (35, '【游戏原声】屏幕之后，更是另一个世界', '/img/songListPic/109951163408189924.jpg', '我们曾与白狼一起踏上寻找女儿的征途\n走遍大陆与史凯利杰群岛，寻遍北方诸国与尼弗迦德 甘当布尔维坎的屠夫\n只为那因意外率而与自己命运相连的辛特拉幼狮\n只为那曾在布洛克莱昂森林惊慌失措的小女孩\n重回自...', 'BGM');
INSERT INTO `song_list` VALUES (36, '极致旋律&空灵飘纱', '/img/songListPic/16508886247860061408b-9622-4ab7-8672-ad4a46f70e0f.jpg', '心在寂静之中冒着烟 寻找安寄所', '欧美');
INSERT INTO `song_list` VALUES (37, '我们的心如此自由 思绪辽远无边', '/img/songListPic/1650888644970d60db39b-928c-4df5-a1d3-cb924857f982.jpg', '我想每个人的心底，都潜藏着一个向往远方的梦，此处已无再多风景，熟悉的地方也不再有惊喜。人心思动，都渴望去流浪天涯，无论以哪种方式。', '欧美');
INSERT INTO `song_list` VALUES (38, '欧美精选 | 嗨 伙计 要来首10w+吗？', '/img/songListPic/1650888663834bb1b7ec2-d183-49cf-8a11-46480f753018.jpg', '个别曲目未收录很可能是由于我没买人家的专辑 emmmm.... \n没买就是没买。没兴趣，不想买，买不起，这答案您满意吗？', '欧美');
INSERT INTO `song_list` VALUES (39, '「高质量英文歌」让你单曲循环的英文歌', '/img/songListPic/18814842976746273.jpg', 'I love the endless sea, I love the rain softly, I love flying snow, I love bringing a bright full moon, but I love the starry night sky.', '欧美');
INSERT INTO `song_list` VALUES (40, '稀 | 奇 | 古 | 怪 | 音 | 效 | 工 | 厂', '/img/songListPic/1650888724124ba36703a-f33a-4730-8355-9b0930aaced6.jpg', '不好意思 在我耳朵化掉之前是能数清的\n\n麻烦大家帮黑哥数数\n\n这些里面藏了多少个稀奇古怪的音效\n\n听归听 收藏某首歌之后我也不知道明天你的日推怎么作妖哈', '欧美');
INSERT INTO `song_list` VALUES (41, '〖欧美女声〗 倾城一刻，我似乎更懂你', '/img/songListPic/16508887622110e84134a-afd5-4f30-ac16-bac9e1da2b9a.jpg', '个性，风格，颜值，行为，年龄段……差不多的人，听歌的兴趣差不多哦', '欧美');
INSERT INTO `song_list` VALUES (42, '『电影配乐』当优雅华尔兹遇上激情探戈', '/img/songListPic/109951163904955394.jpg', '华尔兹有着与生俱来的华贵与优雅。简约却不简单的舞步透出的是舞者相互心灵的体会与传达……', '欧美');
INSERT INTO `song_list` VALUES (43, '妄想去流浪，独自陶醉在自己的世界里。', '/img/songListPic/109951163543366840.jpg', '生活乏味 ，学习一直倒退，工作失意。\n害怕失去，想像和现实的差距将我打败。\n我想要远离这里。', '轻音乐');
INSERT INTO `song_list` VALUES (44, '晚安曲丨我温柔而通透的小宇宙', '/img/songListPic/109951163646671507.jpg', '晚安～今夜好梦啊！', '轻音乐');
INSERT INTO `song_list` VALUES (45, '霍格沃茨自习室', '/img/songListPic/109951163808060526.jpg', '今天读的是书，明天数的是钱。', '轻音乐');
INSERT INTO `song_list` VALUES (46, '性欲与孤独，容易让人误解爱情', '/img/songListPic/109951163924312766.jpg', '平常拍片子时，我喜欢在拍摄现场放一些有画面感的音乐让模特找感觉，抽空整理了一下近期的播放列表，做了这套新歌单，或许适合姑娘们在以下一些场景聆听', '轻音乐');
INSERT INTO `song_list` VALUES (47, '「韩◎甜撩男嗓」徘徊辗转，仍在心上', '/img/songListPic/1650895158072e491c082-1165-4ab7-94e9-3a57df0ccaaf.jpg', '在青山与浪潮之间\n\n徘徊辗转\n\n心上却总记着一个你\n\n封面 ‖ 田柾国（BTS）\n「不定期更换，都是甜酒果」', '日韩-BGM');
INSERT INTO `song_list` VALUES (48, '吃鸡必备BGM', '/img/songListPic/109951163776201870.jpg', '大吉大利 今晚吃鸡\n此歌单适合在素质广场，飞机上，杀完人后，轰炸区天选之人的情况下播放，并不是让你全程听音乐玩游戏。', 'BGM');
INSERT INTO `song_list` VALUES (49, '伤感日语 · 芳华少女的孤独内心', '/img/songListPic/1650895192708ecd80fea-6934-44ce-b13c-69c85eaacfc3.jpg', 'お母さん', '日韩');
INSERT INTO `song_list` VALUES (50, '「予你情书」明年一起去看樱花吧', '/img/songListPic/19079825277149145.jpg', '想和你去看樱花\n看夏日的烟火大会\n看秋日京都岚山的枫叶\n和冬日落雪的小樽\n\n想和你一起去看一场樱花\n看漫天的飞舞的樱花在我们周围\n就这样安静地待着也好彼此交换心事\n\n想予你一封情书\n写尽关于我们的一切...', '日韩');
INSERT INTO `song_list` VALUES (51, '那些年我们听过的韩国歌', '/img/songListPic/18804947371714354.jpg', 'J.Fla，原名Kim Jung Hwa，韩国歌手,歌曲制作人。2013年正式出道并发行首张原创EP《바보 같은 Story》出道后不久就迅速在日本和韩国成为热门话题\n2016年，J.Fla的翻唱作品在国内转载而得到关注，因其甜美声线和惊艳侧颜得到许多人的喜爱。', '日韩');
INSERT INTO `song_list` VALUES (52, '小众而惊艳 ·韩国独立音乐精选集', '/img/songListPic/109951163833244126.jpg', '在无数的灰色版权中找到了它们。\n宝藏一般的旋律，它们不应该被如此埋没。', '日韩');
INSERT INTO `song_list` VALUES (53, '一秒就会中毒的韩语歌单~', '/img/songListPic/109951163515798929.jpg', '愿对这世界温柔以待的人 被温柔以待.', '日韩');
INSERT INTO `song_list` VALUES (54, '［韩语］少女情怀总是诗~', '/img/songListPic/109951162839104712.jpg', '希望所有的少女心事都能梦想成真', '日韩');
INSERT INTO `song_list` VALUES (55, '【燃向】 精选燃曲', '/img/songListPic/19085322835476516.jpg', '封面画师 bilibili Wlop', 'BGM');
INSERT INTO `song_list` VALUES (56, '日系燃向丨此刻光辉 将引领着我们', '/img/songListPic/109951163097151464.jpg', '天空燃尽如灰，\n繁星烧毁似尘，\n那些音乐所带来的力量，化作为光，将引领着我们前行！', '日韩-BGM');
INSERT INTO `song_list` VALUES (57, '日系』有一种调皮叫小清新~', '/img/songListPic/19152393044479439.jpg', '阳光明媚的日子\n心情好到爆炸\n总想要找支歌来抒发一下感情，分享自己的喜悦。\n虽然我听不懂日语，但是并不影响我喜欢它的调皮与清新，舒服的日系小调，让人心情变好~\n\n阳光灿烂，微风拂面，大概就是这个歌单给人的感觉吧~', '日韩');
INSERT INTO `song_list` VALUES (58, '【日系抒情】早晚终相会 忧思情愈深', '/img/songListPic/1650895234022af076e03-0fe9-4c4c-8e15-6a438521ae59.jpg', '瀬を早み 岩にせかるる 滝川の\nわれても末に 逢はむとぞ思ふ', '日韩-轻音乐');
INSERT INTO `song_list` VALUES (59, '日系沙哑女声| 愿这份歌声融化你的心', '/img/songListPic/109951163862683663.jpg', '相信大家都听过沙哑的歌吧，majiko，aimer大家都很熟悉了吧。\n\n这种嗓音真的超温柔呀，这个单选出了些沙哑声线歌手的歌，希望大家喜欢。\n\n愿这温柔的声音可以用听觉的方式带给你一场现实上和想象上的一种迷离感，同时陷入一种与女声错觉般的邂逅。\n\np站id:73189154 画师:gomzi', '日韩');
INSERT INTO `song_list` VALUES (60, '『韩语』 感性伴秋风渐起 随秋意渐浓', '/img/songListPic/109951163606909947.jpg', '走过林荫道\n落叶从眼前划过\n才晓得秋天已经降临了一段时间\n我静静看着你的日子\n似乎还是昨天\n你回头看向别处的那个瞬间\n却已经成为今天\n\n若我们离别\n不要说Good bye\n说See you吧\n如同再次光临的秋天一样\n总...', '日韩');
INSERT INTO `song_list` VALUES (61, '韩‖轻柔小调，治愈系旋律', '/img/songListPic/1650895263180d495f457-2373-41f0-9afa-61f6df701501.jpg', '我爬上全世界的屋顶，\n带着全部的清醒和一只酒瓶。\n— — 张艾嘉《我站在全世界的屋顶》', '日韩-轻音乐');
INSERT INTO `song_list` VALUES (62, '听首老歌 回味永不褪色的华语经典', '/img/songListPic/109951163203287436.jpg', '回味光辉岁月三十年\n\n岁月是一场充满告别的旅程\n\n怀旧\n不是因为那个时代多美好\n而是那时\n你正年轻\n\n百许流年忆往事\n千几往事暖流年', '粤语');
INSERT INTO `song_list` VALUES (63, '【粤语】要有多坚强，才敢念念不忘', '/img/songListPic/1650889777379bba503dd-8e65-4003-aa5a-d044a1e6e774.jpg', '你说\n你不愿意种花\n我不愿看见它\n一点点的凋落\n是的\n为了避免结束\n你避免了一切\n也避免了所有\n\n开始\n你说你喜欢雨滴\n但是你在下雨的时候打伞\n你说你喜欢太阳\n但是你在阳光明媚的时候\n躲在阴凉的地方 \n你说...', '粤语');
INSERT INTO `song_list` VALUES (64, '初听不知曲中意 再听已是曲中人', '/img/songListPic/19101815510024256.jpg', '有时候听到一首歌，觉得旋律很好，歌词很好。但怎么也不能体会到，歌里唱的感情。后来有一天，你遇到了一个人，发生了一段故事。当你再听到那首歌时，就会觉得歌里唱的，都是你的故事。\n每一首你喜欢的歌曲，都附有非一般的意义，因为都唱出了你内心的声音和过往经历，其实你听到的都是你自己，那些年你累积在心里的所有欢乐悲伤，所有故事过往。', '粤语');
INSERT INTO `song_list` VALUES (65, '粤语男声：我爱你依旧如初，不曾改变。', '/img/songListPic/18612532836990988.jpg', '记得曾经看过一段话：爱情不是抱一抱，亲一亲，改个情侣网名，换个情侣头像，就是情侣了...', '粤语');
INSERT INTO `song_list` VALUES (66, '『粤语』虐到心痛的曲 听到泪落的词', '/img/songListPic/165088980804217dd9544-e303-4410-82cc-5eba846a3c19.jpg', '此歌单多为小众粤语，听过太多评论999+的人人传颂的经典歌曲，每次淘到一首鲜为人知人的歌曲，都会非常的惊喜，听歌嘛，最重要的就是自己听着觉得舒服就对啦，不一定非要高热度的啦。此歌单内歌曲不仅旋律打动人心，每首歌词都超虐心，痴情人的爱有时候那么卑微，低到尘埃里，爱到不死不休.....有时候看着歌词听着这些歌情不自禁泪奔，烦请听歌的人，千万不要对号入座，伤害指数超高! 绝对不容错过的小众粤语!', '粤语');
INSERT INTO `song_list` VALUES (67, '富士山下钟无艳 吴哥窟内我本人', '/img/songListPic/109951162869937004.jpg', '男不听七友 女不听钟无艳', '粤语');
INSERT INTO `song_list` VALUES (68, '流行男声||谦谦君子，情浓粤语', '/img/songListPic/109951163193554791.jpg', '慧极必伤，情深不寿，强极则辱，谦谦君子，温润如玉！细数那些唯美男声', '粤语');
INSERT INTO `song_list` VALUES (69, '粤语live||赛赢录音棚流行live', '/img/songListPic/109951163196627760.jpg', '听腻了录影棚里的无杂音歌声？也许换成live会是心的开始', '粤语');
INSERT INTO `song_list` VALUES (70, '从未跟你饮过冰 零度天气看风景', '/img/songListPic/109951163933917463.jpg', '这个世界烂透了 坏透了 我都接受\n\n所有人都习惯于流于表面的热情和爱意 我也接受\n\n但你不行 你得是那个例外才行', '粤语');
INSERT INTO `song_list` VALUES (71, '「致青春」粤语带你回忆', '/img/songListPic/109951163024198570.jpg', '歌听多了 粤语也熟了 \n越听越有感觉', '粤语');
INSERT INTO `song_list` VALUES (72, 'Beyond的辉煌岁月是人生旅途的伴奏', '/img/songListPic/109951163278666363.jpg', 'Beyond是那种有自己独特的风格，有自己的精神，能象征一个时代，也能映射一类人的组合。他们的歌带入感很强，总有共鸣产生，因为他们没有无病呻吟的悲情，也不爱写迎合大众的爱情故事，而是用自己的歌词和声音在诠释着生活，激励着人生', '粤语');
INSERT INTO `song_list` VALUES (73, '听说，钢琴和民谣也很搭配', '/img/songListPic/109951163503924397.jpg', '我们始终会远行，也可能，在最遥远最陌生的地方感知另一个自己，最后发现丢失了好久的钥匙就藏在自己的口袋里', '乐器-轻音乐');
INSERT INTO `song_list` VALUES (74, '新古典钢琴 散落的时光', '/img/songListPic/109951163401615779.jpg', '当时间走过 其风猎猎\n\n覆灭仅存的模糊记忆使昨日土崩瓦解\n\n其实没有什么好担忧的\n\n在生命的内里 不是还有许多\n\n继续延展着的细微线索\n\n以祖先的容颜 来将你形塑\n\n当时间走过 其声簌簌\n\n如狼群之迅疾穿越秋...', '乐器-轻音乐');
INSERT INTO `song_list` VALUES (75, '流行歌曲钢琴演奏', '/img/songListPic/18577348464819001.jpg', '大都是一些华语流行歌曲的钢琴版，也含有少部分这些弹奏者自己创造的钢琴曲与一些他们翻奏的世界较为有名的钢琴曲，希望你们喜欢～\n前50首为精选，请不要错过哦～', '乐器');
INSERT INTO `song_list` VALUES (76, '『钢琴』与流行歌曲的完美邂逅', '/img/songListPic/165089530909541b7f852-de7d-4ce5-9979-a1801f95d271.jpg', '选集是华语流行音乐的钢琴版，或许你会更喜欢钢琴演奏的故事', '乐器');
INSERT INTO `song_list` VALUES (78, '写作业专用', '/img/songListPic/1390882211100783.jpg', '舒缓放松的音乐', '乐器-轻音乐');
INSERT INTO `song_list` VALUES (79, '钢琴的故事', '/img/songListPic/1650895414170d3af007e-d4bd-4585-b3c9-020e84949d60.jpg', '你一定和我一样有烦恼和故事吧', '乐器');
INSERT INTO `song_list` VALUES (80, '各种乐器演绎流行曲', '/img/songListPic/3416182643161526.jpg', '乐器成精系列之用各种成精乐器重新演绎这些流行歌曲，让你耳目一新', '乐器');
INSERT INTO `song_list` VALUES (81, '听, 吉他在唱歌', '/img/songListPic/1390882209600111343.jpg', '听吉他谱写那些熟悉的旋律，遇见不一样的感动，电吉他、尤克里里会客串哦', '乐器');
INSERT INTO `song_list` VALUES (82, '还不快去练琴？', '/img/songListPic/19169985230816413.jpg', '都是自己很喜欢的吉他指弹', '乐器');
INSERT INTO `song_list` VALUES (83, '国风传统器乐赏~~♪', '/img/songListPic/18907201951803673.jpg', '中国音乐是光辉灿烂的中国文化的一个重要组成部分。当古典音乐流泻而出的一刹那间，你可以清楚的看到，在空气中流动的是高山、是流水、是丝竹、是冬雪、是千古的生命，那份说不出、道不尽的感动，就是中国古典音乐之美。', '乐器');
INSERT INTO `song_list` VALUES (84, '『钢琴纯音』八十八个黑白键勾勒出的美', '/img/songListPic/109951162873752063.jpg', '钢琴如生活，是一首永远弹不完的小曲', '轻音乐-乐器');
INSERT INTO `song_list` VALUES (91, '最炸的中文说唱', '/img/songListPic/1650890950960a839c174-032e-4907-9313-c062e3976f07.jpg', '最纯正的【国产】RAP HHHHHHHHHOT SHIT\n\n听的是态度 flow 思想\n\n宁可不火也不要评论下刷X说唱\n\n选什么歌是我的自由 不过欢迎查漏补缺', '说唱');
INSERT INTO `song_list` VALUES (92, '中文说唱 众口难调', '/img/songListPic/16508909626678d0d130d-e120-4096-8741-3952c317dd09.jpg', '不客观目录，喜欢就多听点，纯属个人喜好', '说唱');
INSERT INTO `song_list` VALUES (93, '“就怕rapper说情话”', '/img/songListPic/16508909718149be249e0-88cf-4b36-8a4d-4ae3ee54d64b.jpg', '不要因自己的贪玩和任性 辜负了那些爱你的人.', '说唱');
INSERT INTO `song_list` VALUES (94, '[RAP]中文励志说唱-为梦想努力奋斗', '/img/songListPic/1650890983291302e8bb0-cd58-40bd-9a3b-d616161bf926.jpg', '我曾经天真的以为，用真心对任何人，就可以得到真正的友情，真正的爱情。后来，认识了一些人，经历了一些事，才知道一切都是我以为。\n\n我以为你会一直陪我到最后，可惜不是你，到最后剩下的孤寂我一个人享受就好了，即使再痛又有什么关系。\n\n相遇总是猝不及防，而离别多是蓄谋已久，总有一些人会慢慢淡出你的生活，你要学会接受而不是怀念。留不住的心，就让它飞吧。', '说唱');
INSERT INTO `song_list` VALUES (95, '微醺Hiphop | 扑通 醉倒在酿满爱的银河', '/img/songListPic/1650890990082a57da489-51c0-4c32-a4e4-024b87977b00.jpg', '「本歌单收录了高糖分的中文说唱\n\n“爱”啊 就是从“认识你”到“陪伴你”」\n\n好想跑去找你，然后抱紧你\n\n和你说声我想你，即使不见面\n\n我心里只有一个位置\n\n里面安安稳稳放着你♡\n\n爱情是茫茫宇宙里冥冥...', '说唱');
INSERT INTO `song_list` VALUES (96, 'Hiphop 男声 | 入耳既醉的性感声线', '/img/songListPic/16508912404380d396047-7544-4178-b7f2-547ce7ae050b.jpg', '他就站在那里不说话，嘴角噙笑，眼神清亮，世界都亮了。', '说唱');
INSERT INTO `song_list` VALUES (97, '「你喜欢的」姜云升', '/img/songListPic/16508912499901f82ae24-42e7-42d1-86b5-b71e7d09936c.jpg', '姜云升（1996年-），云南昆明人，《说唱新世代》选手，独立音乐人。', '说唱');
INSERT INTO `song_list` VALUES (98, '住你耳朵里|无限循环的说唱旋律', '/img/songListPic/165089126026628a0ddfa-1f7b-4f9d-bf24-2de9c4bb44a3.jpg', '在耳边循环的那些宝藏说唱，用不同的风格把rapper们的情绪唱给你听；耳机分你一半，一起来找寻你的专属旋律吧！', '说唱');
INSERT INTO `song_list` VALUES (99, '保持对生活的期待，把每天都过得热气腾腾', '/img/songListPic/165089358974901dd37fb-adc1-402e-9880-02d6d2f1f8eb.jpg', '只要好好生活，总会遇见被藏起来的温柔，那些所谓的笨拙也有着无比的真诚和可爱。就祝你遇见的每一场夕阳都好看，永远有捕捉美好的浪漫。', '流行');
INSERT INTO `song_list` VALUES (100, '我要你的光芒，来点亮我内心的诗意', '/img/songListPic/16508936035098c464f7a-3c99-4b5c-b94a-ce5b5681aef6.jpg', '春天会下雪，夏天有大雨，秋天会起风，冬天有艳阳，一年四季会有很多意外，但最迷人的，还是遇见了你。', '流行');
INSERT INTO `song_list` VALUES (101, '十七岁那年心动的人，真的会喜欢好久', '/img/songListPic/16508936121864f464f82-3ada-4ef3-832f-e4e335e01c12.jpg', '教室后门的饮水机\n他借我的一支笔\n运动场的跑道\n放学后的湛蓝天空\n同一副耳机传来的音乐\n十七岁的单车\n……\n一切的一切好像都那么美好\n对于喜欢，或许我们不应该在意得失\n最后没有在一起并不意味着这份喜欢...', '流行');
INSERT INTO `song_list` VALUES (102, '总要允许有人错过你，才能赶上最好的相遇', '/img/songListPic/165089362301835cc6f13-8945-44e2-8522-f7eefbb5cefa.jpg', '行动是治愈恐惧的良药\n而犹豫拖延将不断滋养恐惧\n你要想得到这世界上最美好的东西\n就要让生活看见努力的你', '流行');
INSERT INTO `song_list` VALUES (103, '那些值得循环播放的歌曲.', '/img/songListPic/1650893630330f4490a98-6828-45f7-9902-3d0985a52f06.jpg', '你迟早会明白，前途比感情更重要', '流行');
INSERT INTO `song_list` VALUES (104, '温柔暴击 | 沉溺于男友音的甜蜜乡', '/img/songListPic/16508938327245119609c-7000-457a-905b-4d93350569ff.jpg', '希望以后你的生活\n\n温柔\n有趣\n不必太激烈\n\n三餐\n四季\n不必太匆忙\n\n音乐\n电影\n不必太无聊\n\n迷醉\n沉失\n不必太慌张\n\n一曲男友音\n一梦到天亮', '流行');
INSERT INTO `song_list` VALUES (105, '精选 | 超好听的流行热歌分享', '/img/songListPic/16508938584189122b608-eb15-4a93-a211-a17239126820.jpg', '别让世俗淹没生活的浪漫和热情！', '流行');
INSERT INTO `song_list` VALUES (106, '生活很苦，需要甜甜的你', '/img/songListPic/1650893867359b9200faa-161e-4dd7-8e3b-95cc2b7dd5b2.jpg', '屋檐下避雨，海边放烟花，山顶看日落，牵手逛夜市，还有环游世界，在柴米油盐中伴你走过每一天，都是我想和你做的事。\n\n所以，你快出现吧。', '流行');
INSERT INTO `song_list` VALUES (107, '华语|落日弥漫的橘 天边透亮的星', '/img/songListPic/1650893876521bf28bece-3fc4-459c-86df-e1253709c085.jpg', '一半是热烈，一半是坦荡，一半是真诚，一半是可爱，会永远心怀热望', '流行');
